// Create Reference data table and load data  (Airline code to Name Reference)
snc.sql("DROP TABLE IF EXISTS STAGING_AIRLINEREF")
snc.sql("DROP TABLE IF EXISTS AIRLINEREF")

//----- CREATE TEMPORARY STAGING TABLE TO LOAD PARQUET FORMATTED DATA -----

snc.sql(s"CREATE external TABLE STAGING_AIRLINEREF  USING parquet OPTIONS( path  's3a://AKIAJ73BL2H66NACKJNQ:WdXRSski53xmmtQ3zZQnU1iTL+wqm5XCLSBAMrvf@zeppelindemo/airportdata' )") 

//----- CREATE ROW TABLE -----

snc.sql("CREATE TABLE AIRLINEREF USING row OPTIONS() AS (SELECT CODE, DESCRIPTION FROM STAGING_AIRLINEREF)")
